class Solution {public:    bool isValidSudoku(vector<vector<char>>& board) {        int horizontal[9];        int vertical[9];        int box[9];        int n;                for(int i = 0; i < 9; i++){            horizontal[i] = vertical[i] = box[i] = 0;        }                for(int i = 0; i < 9; i++){            for(int j = 0; j < 9; j++){                if(board[i][j] == '.') continue;                n = board[i][j] - '0';                n = 1<<n;                if( ((horizontal[i] & n) > 0) ||                    ((vertical[j] & n) > 0) ||                    ((box[i/3*3+j/3] & n) > 0)                )                {                                        return false;                }                                horizontal[i] |= n;                vertical[j] |= n;                box[i/3*3+j/3] |= n;                                }        }                return true;    }};